import React from "react";
import html2canvas from "html2canvas";
import { toPng, toJpeg } from "html-to-image";
// import memesData from "../memesData";

export default function Meme() {
  const [meme, setMeme] = React.useState({
    topText: "",
    bottomText: "",
    randomImg: "http://i.imgflip.com/1bij.jpg",
  });

  const [allMeme, setAllMeme] = React.useState([]);
  React.useEffect(
    () =>
      fetch("https://api.imgflip.com/get_memes")
        .then((res) => res.json())
        .then((data) => setAllMeme(data.data.memes)),
    []
  );

  function getMemeImage() {
    const randomNumber = Math.floor(Math.random() * allMeme.length);
    setMeme((prevMeme) => ({
      ...prevMeme,
      randomImg: allMeme[randomNumber].url,
      topText: "",
      bottomText: "",
    }));
  }

  function handleChange(event) {
    setMeme((prevMeme) => ({
      ...prevMeme,
      [event.target.name]: event.target.value,
    }));
  }

  //   const printRef = React.useRef();

  //   const handleDownloadImage = async () => {
  //     const element = document.getElementById("print"),
  //       canvas = await html2canvas(element),
  //       data = canvas.toDataURL("image/jpg"),
  //       link = document.createElement("a");

  //     link.href = data;
  //     link.download = "downloaded-image.jpg";

  //     document.body.appendChild(link);
  //     link.click();
  //     document.body.removeChild(link);
  //   };

  const ref = React.useRef < HTMLDivElement > null;

  const getFileName = (fileType: string) =>
    `${format(new Date(), "'SomeName-'HH-mm-ss")}.${fileType}`;

  const handleDownloadImage = React.useCallback(() => {
    if (ref.current === null) {
      return;
    }
    toJpeg(ref.current, { cacheBust: true })
      .then((dataUrl) => {
        const link = document.createElement("a");
        link.download = `${getFileName("jpg")}`;
        link.href = dataUrl;
        link.click();
      })
      .catch((err) => {
        console.log(err);
      });
  }, [ref]);

  return (
    <main>
      <div className="meme-form">
        <div className="meme-form-input">
          <input
            className="meme-form-input1"
            type="text"
            placeholder="Top Text"
            name="topText"
            value={meme.topText}
            onChange={handleChange}
          />
          <input
            className="meme-form-input2"
            type="text"
            placeholder="Bottom Text"
            name="bottomText"
            value={meme.bottomText}
            onChange={handleChange}
          />
        </div>
        <button className="meme-form-submit" onClick={getMemeImage}>
          Get a new meme image ðŸ–¼
        </button>
      </div>
      <div>
        <div ref={ref} className="meme">
          <img src={meme.randomImg} className="meme-photo" />
          <h2 className="meme--text top">{meme.topText}</h2>
          <h2 className="meme--text bottom">{meme.bottomText}</h2>
        </div>
        <button type="button" onClick={handleDownloadImage}>
          Download as Image
        </button>
      </div>
    </main>
  );
}
