import React from "react";
// import memesData from "../memesData";

export default function Meme() {
  const [meme, setMeme] = React.useState({
    topText: "",
    bottomText: "",
    randomImg: "http://i.imgflip.com/1bij.jpg",
  });

  const [allMeme, setAllMeme] = React.useState([]);
  React.useEffect(
    () =>
      fetch("https://api.imgflip.com/get_memes")
        .then((res) => res.json())
        .then((data) => setAllMeme(data.data.memes)),
    []
  );

  function getMemeImage() {
    const randomNumber = Math.floor(Math.random() * allMeme.length);
    setMeme((prevMeme) => ({
      ...prevMeme,
      randomImg: allMeme[randomNumber].url,
      topText: "",
      bottomText: "",
    }));
  }

  function handleChange(event) {
    setMeme((prevMeme) => ({
      ...prevMeme,
      [event.target.name]: event.target.value,
    }));
  }

  const printRef = React.useRef();

  const handleDownloadImage = async () => {
    const element = printRef.current;
    const canvas = await html2canvas(element);

    const data = canvas.toDataURL("image/jpg");
    const link = document.createElement("a");

    if (typeof link.download === "string") {
      link.href = data;
      link.download = "image.jpg";

      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } else {
      window.open(data);
    }
  };

  return (
    <main>
      <div className="meme-form">
        <div className="meme-form-input">
          <input
            className="meme-form-input1"
            type="text"
            placeholder="Top Text"
            name="topText"
            value={meme.topText}
            onChange={handleChange}
          />
          <input
            className="meme-form-input2"
            type="text"
            placeholder="Bottom Text"
            name="bottomText"
            value={meme.bottomText}
            onChange={handleChange}
          />
        </div>
        <button className="meme-form-submit" onClick={getMemeImage}>
          Get a new meme image ðŸ–¼
        </button>
      </div>
      <div className="meme">
        <img src={meme.randomImg} className="meme-photo" />
        <h2 className="meme--text top">{meme.topText}</h2>
        <h2 className="meme--text bottom">{meme.bottomText}</h2>
      </div>
      <button type="button" onClick={handleDownloadImage}>
        Download as Image
      </button>
    </main>
  );
}
