import React from "react";
import memesData from "../memesData";

export default function Meme() {
  const [memeImage, setMemeImage] = React.useState();

  function getMemeImage() {
    console.log(memesData);
    const memeArray = memesData.data.names;
    const randomNum = Math.floor(Math.random() * 100);
    setMemeImage(memeArray[randomNum].url);
  }

  return (
    <main>
      <div className="meme-form">
        <div className="meme-form-input">
          <input className="meme-form-input1" type="text" value="Shut up" />
          <input
            className="meme-form-input2"
            type="text"
            value="and take my money"
          />
        </div>
        <button className="meme-form-submit" onClick={getMemeImage}>
          Get a new meme image ðŸ–¼
        </button>
      </div>
      <img className="meme-photo" src={memeImage} alt="fdf" />
    </main>
  );
}
